import React from 'react';

const StoreList = ({ stores }) => {
  // Define what happens when a store is selected
  const onSelectStore = (store) => {
    // For now, we'll just log the selected store to the console
    console.log(store);
    // Here, you could update state or perform other actions based on the selected store
  };

  return (
    <div>
      {stores && stores.map(store => (
        <div key={store.id} onClick={() => onSelectStore(store)} style={{ cursor: 'pointer' }}>
          <h3>{store.name}</h3>
          <p>{store.description}</p>
          {/* Display store details, images, etc. */}
        </div>
      ))}
    </div>
  );
};

export default StoreList;
