// src/components/StoreList.js
import React from 'react';

const StoreList = ({ stores }) => {
  // Check if stores is undefined or null before trying to map
  if (!stores) {
    return <div>Loading stores...</div>; // Or any other placeholder content
  }

  return (
    <div>
      {stores.map(store => (
        <div key={store.id} onClick={() => onSelectStore(store)}>
          {store.name}
        </div>
      ))}
    </div>
  );
};

export default StoreList;
