import React, { useState } from 'react';
import QRCode from 'qrcode.react';

const QRModal = ({ onClose, value }) => {
  return (
    <div style={{ position: 'fixed', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: 'auto', minHeight: '200px', padding: '20px', background: 'white', boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', zIndex: 1000, borderRadius: '8px' }}>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={onClose} style={{ float: 'left', marginRight: '16px', background: 'none', border: 'none', cursor: 'pointer', fontWeight: 'bold' }}>X</button>
        <h2>Your Coupon</h2>
      </div>
      <QRCode value={value} size={256} />
      <p style={{ marginTop: '20px' }}>Let the store scan this to redeem the coupon.</p>
    </div>
  );
};

const StoreDetails = ({ store }) => {
  const [showQR, setShowQR] = useState(false);
  const [qrValue, setQRValue] = useState('');

  if (!store) return <div>Select a store to see its details.</div>;

  const handlePurchaseClick = (value) => {
    const qrData = `${store.name}|${value}`;
    setQRValue(qrData);
    setShowQR(true);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ backgroundImage: `url(${store.backdropUrl})`, backgroundSize: 'cover', filter: 'brightness(50%)', height: '200px', width: '100%', position: 'absolute', top: '0', left: '0', zIndex: '-1' }}></div>
      <div style={{ display: 'flex', alignItems: 'center', padding: '20px', zIndex: '1' }}>
        <img src={store.imageUrl} alt={store.name} style={{ marginRight: '20px', width: '100px', height: '100px', objectFit: 'fill' }} />
        <div>
          <h2 style={{ marginBottom: '0.5em' }}>{store.name}</h2>
          <p>{store.description}</p>
        </div>
      </div>
      <div style={{ marginTop: '20px', width: '100%', display: 'flex', justifyContent: 'center', padding: '0 20px' }}>
        <button onClick={() => handlePurchaseClick(20)} style={{ padding: '10px 20px', fontSize: '18px', margin: '20px auto', display: 'block', cursor: 'pointer' }}>$20 Coupon</button>
      </div>

      {showQR && (
        <QRModal onClose={() => setShowQR(false)} value={qrValue} />
      )}
    </div>
  );
};

export default StoreDetails;
