import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import StoreList from './components/StoreList';
import Dashboard from './components/Dashboard';

const App = () => {
  const [stores, setStores] = useState([]);

  useEffect(() => {
    // Initialize or fetch stores when the app loads
    const initialStores = initializeStores();
    setStores(initialStores);
  }, []);

  // Function to add a new store, updating both state and localStorage
  const addStore = (newStore) => {
    const updatedStores = [...stores, { ...newStore, id: Date.now().toString() }];
    setStores(updatedStores);
    localStorage.setItem('stores', JSON.stringify(updatedStores));
  };

  return (
    <Router>
      <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
        <nav>
          <Link to="/" style={{ marginRight: '12px' }}>Stores</Link>
          <Link to="/dashboard">Dashboard</Link>
        </nav>
      </div>
      <Routes>
        <Route path="/" element={<StoreList stores={stores} />} />
        <Route path="/dashboard" element={<Dashboard addStore={addStore} />} />
      </Routes>
    </Router>
  );
};

// Utility function to get stores or initialize them
function initializeStores() {
  // Attempt to fetch existing stores from localStorage
  const existingStores = JSON.parse(localStorage.getItem('stores'));

  if (!existingStores) {
    // Default store data
    const defaultStore = [{
      id: '1',
      name: 'Default Store',
      description: 'This is a default store.',
      imageUrl: 'https://via.placeholder.com/150',
      backdropUrl: 'https://via.placeholder.com/300',
      coupons: [{ id: 'c1', value: 5 }, { id: 'c2', value: 10 }, { id: 'c3', value: 20 }],
    }];

    // Initialize localStorage with the default store
    localStorage.setItem('stores', JSON.stringify(defaultStore));

    return defaultStore;
  }

  return existingStores;
}


export default App;
