import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import StoreList from './components/StoreList';
import StoreDetails from './components/StoreDetails';
import Dashboard from './components/Dashboard';

const App = () => {
  const [stores, setStores] = useState([/* Initial stores data here */]);
  const [selectedStore, setSelectedStore] = useState(null);

  const handleAddStore = (newStore) => {
    const updatedStores = [...stores, { ...newStore, id: Math.random().toString() }];
    setStores(updatedStores);
  };

  return (
    <Router>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <nav style={{ padding: '20px' }}>
          <Link to="/" style={{ marginRight: '20px' }}>Stores</Link>
          <Link to="/dashboard">Dashboard</Link>
        </nav>
        <Routes>
          <Route path="/" element={
            <div style={{ display: 'flex', width: '100%' }}>
              <div style={{ width: '30%', borderRight: '1px solid #ccc' }}>
                <StoreList stores={stores} onSelectStore={setSelectedStore} />
              </div>
              <div style={{ width: '70%' }}>
                <StoreDetails store={selectedStore} />
              </div>
            </div>
          } />
          <Route path="/dashboard" element={<Dashboard addStore={handleAddStore} />} />
        </Routes>
      </div>
    </Router>
  );
};

// Utility function to get stores or initialize them
function initializeStores() {
  // Attempt to fetch existing stores from localStorage
  const existingStores = JSON.parse(localStorage.getItem('stores'));

  if (!existingStores) {
    // Default store data
    const defaultStore = [{
      id: '1',
      name: 'Default Store',
      description: 'This is a default store.',
      imageUrl: 'https://via.placeholder.com/150',
      backdropUrl: 'https://via.placeholder.com/300',
      coupons: [{ id: 'c1', value: 5 }, { id: 'c2', value: 10 }, { id: 'c3', value: 20 }],
    }];

    // Initialize localStorage with the default store
    localStorage.setItem('stores', JSON.stringify(defaultStore));

    return defaultStore;
  }

  return existingStores;
}


export default App;
