function StoreList({ onSelectStore }) {
    const stores = [
      { id: 1, name: 'Store 1', description: 'Description for Store 1' },
      // Add more stores here
    ];

    return (
      <div style={{ width: '20%', overflowY: 'auto' }}>
        {stores.map(store => (
          <div key={store.id} onClick={() => onSelectStore(store)} style={{ cursor: 'pointer', padding: '10px', borderBottom: '1px solid #ccc' }}>
            {store.name}
          </div>
        ))}
      </div>
    );
  }

  export default StoreList;
